import React from 'react';
import Card from './Card';
import makeRequest from '../util/makeRequest';
import { GET_BLOG_DATA } from '../constants/apiEndpoints';

const Main = () => {
  const sortByTitleHandler=(a,b)=>{
    if(a.title>b.title){
      return 1;
    }
    else if(a.title<b.title){
      return -1;
    }
    else{
      return a.id-b.id;
    }
  };
  const [error,setError]=React.useState(null);
  const [blogsData,setBlogsData]=React.useState([]);

  React.useEffect(()=>{

    makeRequest(GET_BLOG_DATA)
    .then((data)=>{
      setBlogsData(data);
    })
    .catch((err)=>{
      setError(err.message);
    })
  },[])

  if(error){
    return <div>{error}</div>
  }
  else{
    return (
      blogsData?
      <main>
        <div className="cards">

          {blogsData.sort(sortByTitleHandler)}
          {blogsData.map((blogData)=><Card id={blogData.id} title={blogData.title} image={blogData.image} reading_time={blogData.reading_time} description={blogData.description} claps={blogData.claps} date={blogData.date} liked={blogData.liked}/>)}
        </div>
      </main>
      :<div>Loading...</div>
    );
  }
};
export default Main;
