import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHandsClapping, faHeart } from '@fortawesome/free-solid-svg-icons';

const Card = (props) => {
  const [numClaps, setNumClaps] = React.useState(0);
  const addClapHandler = () => {
    setNumClaps(numClaps + 1);
  };

  const [heart, setHeart] = React.useState('black');
  return (
    <div>
      <div className="card">
        <img src={props.image} alt="" />
        <div className="timestamp">
          <p className="date">2nd January, 2018</p>
          <p className="time">{props.reading_time}</p>
        </div>
        <h3>{props.title}</h3>
        <p cla>
          {props.description}
        </p>
        <hr />
        <div className="icons">
          <div className="clap">
            <FontAwesomeIcon icon={faHandsClapping} onClick={addClapHandler} data-testid='clapIcon' />
            <p className="clapCount" data-testid='clapCount'>{numClaps}</p>
          </div>
          <div className="like">
            <FontAwesomeIcon
              icon={faHeart}
              color={heart}
              onClick={(e) => {
                const newColor = heart === 'black' ? 'red' : 'black';
                setHeart(newColor);
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};
export default Card;
