import axios from 'axios';
import { BACKEND_URL } from '../constants/apiEndpoints';

const makeRequest = async (apiEndpoint, dynamicConfig = {}) => {
    const config = {
        baseURL: BACKEND_URL,
        url: apiEndpoint.url,
        method: apiEndpoint.method,
        ...dynamicConfig
    };
    const data = (await axios(config));
    return data;
}
export default makeRequest;
