import React from 'react';
import Card from './Card';
import makeRequest from '../util/makeRequest';
import { GET_BLOG_DATA } from '../constants/apiEndpoints';

const Main = () => {

  const [error,setError]=React.useState(null);
  const [blogsData,setBlogsData]=React.useState([]);

  React.useEffect(()=>{

    makeRequest(GET_BLOG_DATA)
    .then((data)=>{
      setBlogsData(data);
    })
    .catch((err)=>{
      setError(err.message);
    })
  },[])

  if(error){
    return <div>{error}</div>
  }
  else{
    return (
      blogsData?
      <main>
        <div className="cards">
          {blogsData.map((blogData)=><Card title={blogData.title} image={blogData.image} reading_time={blogData.reading_time} description={blogData.description}/>)}
        </div>
      </main>
      :<div>Loading...</div>
    );
  }
};
export default Main;
